        for (int right = 0; right < nums.size(); right++) {
            cur += nums[right];
            while (cur >= goal && left <= right) {
                cur -= nums[left];
                left++;
            }
            res += left;
        }
        return res;
    }
int numSubarraysWithSum(vector<int>& nums, int goal) {
    return my_fun(nums, goal) - my_fun(nums, goal + 1);
}
